import React, { Component } from "react";
import axios from "axios";
import Joke from "./Joke";
import "./JokeList.css";


class JokeList extends Component {
  static defaultProps = {
    numJokesToGet: 10,
  };

  // const [jokes, setJokes] = useState([]);
  constructor(props) {
    super(props);
    this.state = {
      jokes: [],
    };
    this.generateNewJokes = this.generateNewJokes.bind(this);
    this.resetVotes = this.resetVotes.bind(this);
    this.toggleLock = this.toggleLock.bind(this);
    this.vote = this.vote.bind(this);
  }

  /* get jokes if there are no jokes */
  componentDidUpdate() {
    if (!this.jokes) {
      this.getJokes();
    }
  }

  // useEffect(function() {
  async getJokes() {
    try {
      let jokes = this.state.jokes;
      let jokeObj = {
        text: null,
        votes: 0,
        id: null, 
        locked: false
      };
      let seenJokes = new Set(jokes.map((joke) => joke.id));
      let jokeVotes = JSON.parse(
        window.localStorage.getItem("jokeVotes") || "{}"
      );

      for (let i = 0; i < this.numJokesToGet; i++) {
        let jokeRes = await axios.get("https://icanhazdadjoke.com", {
          headers: { Accept: "application/json" },
        });
        if (!seenJokes.jokeRes.data.id) {
          seenJokes.add(jokeRes.data.id);
          jokeVotes[resp.data.id] = jokeVotes[resp.data.id];
          jokeObj.text = jokeRes;
          this.state.jokes.push(jokeObj);
        } else {
          console.error("duplicate");
        }
      }

      this.setState({ jokes });
      window.localStorage.setItem("jokeVotes", JSON.stringify(jokeVotes));
    } catch (e) {
      console.log(e);
    }
  }
  generateNewJokes() {
    this.setState((st) => ({ jokes: st.jokes.filter((joke) => joke.locked) }));
  }

  resetVotes() {
    window.localStorage.setItem("jokeVotes", "{}");
    this.setState((st) => ({
      jokes: st.jokes.map((joke) => ({ ...joke, votes: 0 })),
    }));
  }

  /* change vote for this id by delta (+1 or -1) */

  vote(id, delta) {
    let jokeVotes = JSON.parse(window.localStorage.getItem("jokeVotes"));
    jokeVotes[id] = (jokeVotes[id] || 0) + delta;
    window.localStorage.setItem("jokeVotes", JSON.stringify(jokeVotes));
    this.setState((st) => ({
      jokes: st.jokes.map((joke) =>
        joke.id === id ? { ...joke, votes: joke.votes + delta } : joke
      ),
    }));
  }

  toggleLock(id) {
    this.setState((st) => ({
      jokes: st.jokes.map((joke) =>
        joke.id === id ? { ...joke, locked: !joke.locked } : joke
      ),
    }));
  }

  render() {
    let showJokes = this.state.jokes.sort((a, b) => b.votes - a.votes);
    let allLocked =
      showJokes.filter((joke) => joke.locked).length === this.props.numJokesToGet;
    return (
      <div className="JokeList">
        <button
          className="JokeList-getmore"
          onClick={this.generateNewJokes}
          disabled={allLocked}
        >
          Get New Jokes
        </button>
        <button className="JokeList-getmore" onClick={this.resetVotes}>
          Reset Vote Counts
        </button>
        {showJokes.map((joke) => (
          <Joke
            text={joke.text}
            key={joke.id}
            id={joke.id}
            votes={joke.votes}
            vote={this.vote}
            locked={joke.locked}
            toggleLock={this.toggleLock}
          />
        ))}
        {showJokes.length < this.props.numJokesToGet ? (
          <div className="loading">
            <i className="fas fa-4x fa-spinner fa-spin" />
          </div>
        ) : null}
      </div>
    );
  }
}

export default JokeList;
