import React, { Component } from "react";
import axios from "axios";
import Joke from "./Joke";
import "./JokeList.css";


class JokeList extends Component {
  static defaultProps = {
    numJokesToGet: 10
  }

  // const [jokes, setJokes] = useState([]);
  constructor(props) {
    super(props);
    this.state = {
      jokes: []
    }
    this.generateNewJokes = this.generateNewJokes.bind(this);
    this.resetVotes = this.resetVotes.bind(this);
    this.toggleLock = this.toggleLock.bind(this);
    this.vote = this.vote.bind(this);
  }

  /* get jokes if there are no jokes */
  componentDidUpdate() {
    if (!this.jokes) {
      this.getJokes()
    }
  }

  // useEffect(function() {
  async getJokes() {
    // let jokeList = this.state.jokes;
    // let seenJokes = this.state.jokes
    // let seenJokes = new Set();

    try {
      let jokes = this.state.jokes;
      let jokeObj = {
        text: null,
        votes: 0,
      };
      let seenJokes = []
      let jokeVotes = JSON.parse(
        window.localStorage.getItem("jokeVotes") || "{}"
      )

      for (let i = 0; i < this.numJokesToGet; i++) {
        let joke = await axios.get("https://icanhazdadjoke.com", {
          headers: { Accept: "application/json" },
        });
        if (!seenJokes.has(joke.id)) {
          seenJokes.add(joke.id);
          jokeObj.text = joke;
          this.state.jokes.push(jokeObj);
        } else {
          console.error("duplicate found!");
        }
      }

      this.setState({ jokes });
      window.localStorage.setItem("jokeVotes", JSON.stringify(jokeVotes));
    } catch (e) {
      console.log(e);
    }


  // if (jokes.length === 0) getJokes();
  // }, [jokes, numJokesToGet]);

  /* empty joke list and then call getJokes */

  // function generateNewJokes() {
  //   setJokes([]);
  // }

  /* change vote for this id by delta (+1 or -1) */

  // function vote(id, delta) {
  //   setJokes(allJokes =>
  //     allJokes.map(j => (j.id === id ? { ...j, votes: j.votes + delta } : j))
  //   );
  // }

  /* render: either loading spinner or list of sorted jokes. */

  // if (jokes.length) {
  //   let sortedJokes = [...jokes].sort((a, b) => b.votes - a.votes);
  }

  render() {

    let showJokes = this.state.jokes.sort((a, b) => b.votes - a.votes);
    let allLocked =
      showJokes.filter((j) => j.locked).length === this.props.numJokesToGet;
    return (
      <div className="JokeList">
        <button
          className="JokeList-getmore"
          onClick={this.generateNewJokes}
          disabled={allLocked}
        >
          Get New Jokes
        </button>
        <button className="JokeList-getmore" onClick={this.resetVotes}>
          Reset Vote Counts
        </button>
        {showJokes.map((j) => (
          <Joke
            text={j.joke}
            key={j.id}
            id={j.id}
            votes={j.votes}
            vote={this.vote}
            locked={j.locked}
            toggleLock={this.toggleLock}
          />
        ))}
        {showJokes.length < this.props.numJokesToGet ? (
          <div className="loading">
            <i className="fas fa-4x fa-spinner fa-spin" />
          </div>
        ) : null}
      </div>
    );
  };
}

export default JokeList;
